import React from 'react';
import ReactDOM from 'react-dom/client';
import { useState, useRef, useEffect } from "react";
import './ChatContent'
import ChatContent from './ChatContent';

function Chat() {

    var [chatContent, setChatContent] = useState(
        [
            {
                "type": 1,
                "content": "Hello"
            },
            {
                "type": 0,
                "content": "Xin Chao"
            }
        ]
    );

    var [inputValue, SetInputValue] = useState("");

    const chatRef = useRef(null);

    useEffect(() => {
        // Scroll to the bottom whenever messages change
        chatRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [chatContent]);

    var handleChange = function (e) {
        SetInputValue(e.target.value);
    }

    var sendMessage = function () {

    }

    var getAnswer = function () {
        var botContent = {
            "type": 0,
            "content": "Thank you"
        }
        setChatContent([...chatContent, botContent]);
    }

    var generateMessage = function () {
        var userContent = {
            "type": 1,
            "content": inputValue
        }
        setChatContent([...chatContent, userContent]);
        getAnswer();
                SetInputValue("");

    }
    return (
        <div class="chat">
            <div class="chat-header clearfix">
                <div class="row">
                    <div class="col-lg-6">
                        <a href="javascript:void(0);" data-toggle="modal" data-target="#view_info">
                            <img src="images/ai_agent.avif" alt="avatar" />
                        </a>
                        <div class="chat-about">
                            <h6 class="m-b-0">AI Agent</h6>
                            <small>Last seen: Online</small>
                        </div>
                    </div>
                </div>
            </div>
            <div class="chat-history" id="chat-context">
                <ChatContent message={chatContent} />
                <div ref={chatRef} />
            </div>
            <div class="chat-message clearfix">
                <div class="input-group mb-0">
                    <div class="input-group-prepend">
                        <span class="input-group-text" onClick={generateMessage}><i class="fa fa-send"></i></span>
                    </div>
                    <input type="text" class="form-control" placeholder="Enter text here..." value={inputValue} onChange={handleChange} />
                </div>
            </div>
        </div>
    );
}

export default Chat;
