import React from 'react';
import ReactDOM from 'react-dom/client';
import { useState, useRef, useEffect } from "react";
import axios from "axios";
import './ChatContent'
import ChatContent from './ChatContent';

function Chat() {

    var [chatContent, setChatContent] = useState(
        [
            {
                "type": 1,
                "content": "Hello"
            },
            {
                "type": 0,
                "content": "Xin Chao"
            }
        ]
    );

    var [inputValue, SetInputValue] = useState("");

    const chatRef = useRef(null);

    useEffect(() => {
        // Scroll to the bottom whenever messages change
        chatRef.current?.scrollIntoView({ behavior: "smooth" });
    }, [chatContent]);

    var handleChange = function (e) {
        SetInputValue(e.target.value);
    }

    var sendMessage = function () {

    }

    var getAnswer = async function () {
        SetInputValue("");
        var botContent = {
            "type": 0,
            "content": "Thank you"
        }
        // try {
        //     var data = [
        //         {
        //             "sessionId": "b9e4bc69a0694a15b110cd7ed1e8303b",
        //             "action": "sendMessage",
        //             "chatInput": "bạn biết mình tên gì không"
        //         }
        //     ];
        //     var response = await axios.post("http://localhost:5678/webhook/908725f2-e8a6-4142-8467-7565df1e8457", data, {
        //         headers: {
        //             "Authorization": "Basic " + btoa("test:123"),
        //         },
        //         httpsAgent: new (require("https").Agent)({ rejectUnauthorized: false })
        //     });
        //     console.log(response);
        // } catch (error) {
        //     console.error("Error fetching data:", error);
        // }

        var data = [
            {
                "sessionId": "b9e4bc69a0694a15b110cd7ed1e8303b",
                "action": "sendMessage",
                "chatInput": "bạn biết mình tên gì không"
            }
        ];
        try {
            await fetch("http://localhost:5", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    // "Authorization": "Basic " + btoa("ngonc:ngonc"),
                    "ngonc":"ngonc"
                },
                body: JSON.stringify(data),
            }).then(response => response)
                .then(data => console.log("Fetched data:", data))
                .catch(error => console.error("Error fetching data:", error));
        } catch (error) {
            console.error("Error:", error);
        }

        return botContent;
    }

    var generateMessage = async function () {
        var userContent = {
            "type": 1,
            "content": inputValue
        }
        setChatContent([...chatContent, userContent, await getAnswer()]);
    }
    return (
        <div class="chat">
            <div class="chat-header clearfix">
                <div class="row">
                    <div class="col-lg-6">
                        <a href="javascript:void(0);" data-toggle="modal" data-target="#view_info">
                            <img src="images/ai_agent.avif" alt="avatar" />
                        </a>
                        <div class="chat-about">
                            <h6 class="m-b-0">AI Agent</h6>
                            <small>Last seen: Online</small>
                        </div>
                    </div>
                </div>
            </div>
            <div class="chat-history" id="chat-context">
                <ChatContent message={chatContent} />
                <div ref={chatRef} />
            </div>
            <div class="chat-message clearfix">
                <div class="input-group mb-0">
                    <div class="input-group-prepend">
                        <span class="input-group-text" onClick={generateMessage}><i class="fa fa-send"></i></span>
                    </div>
                    <input type="text" class="form-control" placeholder="Enter text here..." value={inputValue} onChange={handleChange} />
                </div>
            </div>
        </div>
    );
}

export default Chat;
